mapper.select:



        AND d.dept_name like CONCAT('%', #{deptName}, '%')


        AND d.status = #{status}

    AND d.enabled = 1

    ${params.dataScope}

order by d.sort

select d.dept_id as id,d.parent_id,d.dept_name as title from sys_dept d
d.enabled = 1
${params.dataScope}

mapper.update:
update sys_dept set ancestors =

    when #{item.id} then #{item.ancestors}
,
where id in

    #{item.id}

update sys_dept d
parent_id = #{parentId}, `dept_name` = #{deptName}, ancestors = #{ancestors}, sort = #{sort}, status = #{status},
where d.dept_id = #{deptId}

mapper.select:
select count(1) from sys_dept

     and dept_id = #{deptId} 
     and parent_id = #{parentId} 
    AND enabled = 1
